import { useEffect, useState } from "react";

function TeacherDashboard() {
  const [assignments, setAssignments] = useState([]); // Ensure assignments is an array
  const [error, setError] = useState(null);

  useEffect(() => {
    fetch("http://localhost:5000/api/assignments") // Update API endpoint if needed
      .then((res) => res.json())
      .then((data) => {
        if (Array.isArray(data)) {
          setAssignments(data); // ✅ Ensure data is an array before setting it
        } else {
          setAssignments([]); // ✅ Fallback to an empty array
        }
      })
      .catch((err) => {
        console.error("Error fetching assignments:", err);
        setError("Failed to load assignments");
        setAssignments([]); // Ensure it's an array even on error
      });
  }, []);

  if (error) {
    return <div>Error: {error}</div>;
  }

  return (
    <div>
      <h1>Teacher Dashboard</h1>
      <h2>Assignments</h2>
      {assignments.length === 0 ? (
        <p>No assignments available.</p>
      ) : (
        <ul>
          {assignments.map((assignment, index) => (
            <li key={index}>{assignment.title} - {assignment.className}</li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default TeacherDashboard;
