import { useState, useEffect } from "react";

function TeacherDashboard() {
  const [assignments, setAssignments] = useState([]);
  const [newAssignment, setNewAssignment] = useState({ title: "", description: "", dueDate: "", classAssigned: "" });

  useEffect(() => {
    fetch("http://localhost:5000/api/assignments/progress/TEACHER_ID")
      .then((res) => res.json())
      .then((data) => setAssignments(data));
  }, []);

  const handleCreateAssignment = async () => {
    await fetch("http://localhost:5000/api/assignments/create", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ ...newAssignment, teacherId: "TEACHER_ID" }),
    });
    alert("Assignment Created!");
  };

  return (
    <div>
      <h1>Teacher Dashboard</h1>
      <h2>Create Assignment</h2>
      <input type="text" placeholder="Title" onChange={(e) => setNewAssignment({ ...newAssignment, title: e.target.value })} />
      <input type="text" placeholder="Description" onChange={(e) => setNewAssignment({ ...newAssignment, description: e.target.value })} />
      <input type="date" onChange={(e) => setNewAssignment({ ...newAssignment, dueDate: e.target.value })} />
      <input type="text" placeholder="Class Assigned" onChange={(e) => setNewAssignment({ ...newAssignment, classAssigned: e.target.value })} />
      <button onClick={handleCreateAssignment}>Create</button>

      <h2>Assignment Progress</h2>
      {assignments.map((assignment) => (
        <div key={assignment._id}>
          <h3>{assignment.title}</h3>
          <p>Pending Submissions:</p>
          {assignment.submissions.length === 0 ? <p>All students submitted!</p> : assignment.submissions.map((sub) => <p>{sub.studentId.name}</p>)}
        </div>
      ))}
    </div>
  );
}

export default TeacherDashboard;
